    vm.showExtraOrdersOrAddItem = function () {
        vm.tempItemWithExtraOrders = JSON.parse(JSON.stringify(vm.orderListOfCategory));
        var extraOrdersList = vm.checkExtraOrders(vm.tempItemWithExtraOrders);
        if (extraOrdersList.length > 0) {

            //Show extra orders modal and make user select.
            vm.currentItemsWithExtraOrdersList = extraOrdersList;
            // vm.tempItemPrice = parseFloat(parseFloat(vm.tempItemWithExtraOrders.PR).toFixed(2));
            vm.tempItemWithExtraOrders.Price = parseFloat(parseFloat(vm.tempItemWithExtraOrders.Price).toFixed(2));
            vm.showModal('extraOrdersModal');
            vm.currentNavigationSectionIndex = 3;
            vm.currentModalNavigationSectionIndex = 0;
            vm.currentModal = vm.modalEnum.ExtraOrdersModal;
            vm.currentItemIndexWithExtraOrdersList = 0;
            vm.currentExtraOrderSelectionType = 0;
            if (vm.currentItemsWithExtraOrdersList[0].SpecialOrder.length < 1) {

                vm.currentExtraOrderSelectionType = 1;
            }
            vm.currentExtraOrderIndex = 0;
        } else {

            vm.addItemToBasketWithoutExtraOrders();
        }
    };

    vm.addItemToBasketWithoutExtraOrders = function () {

        vm.orderListOfCategory.Amount = 1;
        var itemIndexInBasket = vm.searchBasketForItem();
        if (itemIndexInBasket > -1) {

            vm.basketList[itemIndexInBasket].Amount += 1;
        } else {

            vm.basketList.push(vm.orderListOfCategory);
        }
        vm.basketList = JSON.parse(angular.toJson(vm.basketList));
        vm.totalBasketPrice += parseFloat(parseFloat(vm.orderListOfCategory.Price).toFixed(2));
    };

    vm.addItemToBasketWithExtraOrders = function () {

        vm.addItemAttempted = true;

        vm.checkSpecialOrdersValidation();
        if (vm.extraOrdersValidation) {

            vm.tempItemWithExtraOrders.Amount = 1;
            vm.basketList.push(vm.tempItemWithExtraOrders);
            vm.basketList = JSON.parse(angular.toJson(vm.basketList));
            vm.totalBasketPrice += parseFloat(parseFloat(vm.tempItemWithExtraOrders.Price).toFixed(2));
            vm.hideModal();
            vm.currentNavigationSectionIndex = 1;
            vm.addItemAttempted = false;
            vm.extraOrdersValidation = true;
        }
    };

    vm.checkSpecialOrdersValidation = function () {

        for (var i = 0; i < vm.currentItemsWithExtraOrdersList.length; i++) {
            if (vm.currentItemsWithExtraOrdersList[i].SpecialOrder.length > 0) {
                var specialOrderSelected = false;
                for (var j = 0; j < vm.currentItemsWithExtraOrdersList[i].SpecialOrder.length; j++) {
                    if (vm.currentItemsWithExtraOrdersList[i].SpecialOrder[j].Selected) {
                        specialOrderSelected = true;
                    }
                }
                if (!specialOrderSelected) {
                    vm.extraOrdersValidation = false;
                    return;
                }
            }
        }
        vm.extraOrdersValidation = true;
    };

    vm.incrementItemInBasket = function () {

        vm.basketList[vm.currentBasketIndex].Amount += 1;
        vm.basketList = JSON.parse(angular.toJson(vm.basketList));
        vm.totalBasketPrice += parseFloat(parseFloat(vm.basketList[vm.currentBasketIndex].Price).toFixed(2));
    };

    vm.decrementItemInBasket = function () {

        var itemPrice = vm.basketList[vm.currentBasketIndex].Price;
        try {

            if (vm.basketList[vm.currentBasketIndex].Amount > 1) {
                vm.basketList[vm.currentBasketIndex].Amount -= 1;
            } else {
                vm.basketList.splice(vm.currentBasketIndex, 1);
                if (vm.basketList.length > 0) {
                    vm.prevBasketItem();
                } else {
                    vm.prevNavigationSection();
                }
            }
            vm.totalBasketPrice -= parseFloat(parseFloat(itemPrice).toFixed(2));
        } catch (err) {

            console.log('An error occured when decrementing item in basket: ' + err.message);
        }
    };

    vm.removeItemFromBasket = function () {

        var itemIndexInBasket = vm.searchBasketForItem();
        if (itemIndexInBasket > -1) {

            if (vm.basketList[itemIndexInBasket].Amount > 1) {
                vm.basketList[itemIndexInBasket].Amount -= 1;
            } else {
                vm.basketList.splice(itemIndexInBasket, 1);
            }
            vm.totalBasketPrice -= parseFloat(parseFloat(vm.orderListOfCategory[vm.currentOrderIndex].Price).toFixed(2));
        }
    };

    vm.checkExtraOrders = function (item) {

        var itemsWithExtraOrdersList = [];
        if (item.NodeTypeName == 'Physical') {

            var extraOrdersExists = vm.checkItemForExtraOrders(item);
            if (extraOrdersExists) {
                itemsWithExtraOrdersList.push(item);
            }
            return itemsWithExtraOrdersList;
        } else {

            var itemsInBasket = item;
            if (itemsInBasket != null && itemsInBasket.length > 0) {
                for (var i = 0; i < itemsInBasket.length; i++) {
                    var subItem = itemsInBasket[i];
                    var extraOrdersExists = vm.checkItemForExtraOrders(subItem);
                    if (extraOrdersExists) {
                        itemsWithExtraOrdersList.push(subItem);
                    }
                }
                return itemsWithExtraOrdersList;
            } else {

                var extraOrdersExists = vm.checkItemForExtraOrders(item);
                if (extraOrdersExists) {
                    itemsWithExtraOrdersList.push(item);
                }
                return itemsWithExtraOrdersList;
            }
        }
    };

    vm.checkItemForExtraOrders = function (item) {

        var extraOrdersOfItem = item.OrderExtra;
        var specialOrdersOfItem = item.SpecialOrder;
        if (extraOrdersOfItem != null && extraOrdersOfItem.length > 0 || specialOrdersOfItem != null && specialOrdersOfItem.length > 0) {
            return true;
        } else {
            return false;
        }
    };

    vm.searchBasketForItem = function () {

        var exists = false;
        for (var i = 0; i < vm.basketList.length; i++) {
            if (vm.basketList[i].TreeItemsGUID == vm.orderListOfCategory.TreeItemsGUID) {
                exists = true;
                return i;
            }
        }
        if (!exists) {
            return -1;
        }
    };

    vm.showModal = function (modalId) {

        document.getElementById(modalId).style.display = 'block';
    };

    vm.hideModal = function () {

        document.getElementById('myModal').style.display = 'none';
        document.getElementById('orderCompletedModal').style.display = 'none';
        document.getElementById('extraOrdersModal').style.display = 'none';
        document.getElementById('extraOrdersModal').style.display = 'none';
        document.getElementById('orderNOTCompletedModal').style.display = 'none';
    };

    vm.runButtonAction = function () {

        switch (vm.currentButtonIndex) {
            case 0:
                if (vm.currentNavigationSectionIndex == 1) {
                    vm.showModal('myModal');
                    vm.currentNavigationSectionIndex = 3;
                    vm.currentModal = vm.modalEnum.OrderCompleteModal;
                } else if (vm.currentNavigationSectionIndex == 2) {
                    vm.incrementItemInBasket();
                } else if (vm.currentNavigationSectionIndex == 3) {
                    vm.sendOrder();
                }
                break;
            case 1:
                if (vm.currentNavigationSectionIndex == 1) {
                    vm.showExtraOrdersOrAddItem();
                } else if (vm.currentNavigationSectionIndex == 2) {
                    vm.decrementItemInBasket();
                } else if (vm.currentNavigationSectionIndex == 3) {
                    vm.sendOrder();
                }
                break;
            case 2:
                if (vm.currentNavigationSectionIndex == 1) {
                    vm.removeItemFromBasket();
                }
                break;
        }
    };

    vm.selectExtraOrderItem = function () {

        if (vm.currentExtraOrderSelectionType == 0) {

            if (vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].SpecialOrder[vm.currentExtraOrderIndex].Selected) {
                vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].SpecialOrder[vm.currentExtraOrderIndex].Selected = false;
            } else {
                for (var i = 0; i < vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].SpecialOrder.length; i++) {
                    vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].SpecialOrder[i].Selected = false;
                }
                vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].SpecialOrder[vm.currentExtraOrderIndex].Selected = true;
            }
            if (vm.addItemAttempted) {
                vm.checkSpecialOrdersValidation();
            }
        } else {

            if (vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].Selected) {
                vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].Selected = false;
                vm.tempItemWithExtraOrders.PR -= parseFloat(parseFloat(vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].PR).toFixed(2));
                // vm.tempItemPrice -= parseFloat(parseFloat(vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].PR).toFixed(2));
            } else {
                vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].Selected = true;
                vm.tempItemWithExtraOrders.PR += parseFloat(parseFloat(vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].PR).toFixed(2));
                // vm.tempItemPrice += parseFloat(parseFloat(vm.currentItemsWithExtraOrdersList[vm.currentItemIndexWithExtraOrdersList].OrderExtra[vm.currentExtraOrderIndex].PR).toFixed(2));
            }
        }
    };

