    vm.handleKeypress = function (args) {
        if (args.state == 'tvpreview') {
            if (vm.currentNavigationSectionIndex != 2 && (args.keyname == 'Key_Menu' || args.keyname == 'Key_Home')) {
                vm.sendGoToMenuMessage();
            } else {
                switch (vm.currentNavigationSectionIndex) {
                    case 0:
                        vm.handleCategoryNavigation(args);
                        $scope.$apply();
                        break;
                    case 1:
                        vm.handleChannelNavigation(args);
                        $scope.$apply();
                        break;
                    case 2:
                        vm.handleFullscreenNavigation(args);
                        $scope.$apply();
                        break;
                    case 3:
                        vm.handleSubtitleAudioNavigation(args);
                        $scope.$apply();
                        break;
                }
            }
        }
    };

    vm.handleCategoryNavigation = function (args) {
        vm.scrollHeight = document.getElementById('tvCategoryContent').scrollHeight;
        switch (args.keyname) {
            case 'Key_Ok':
                vm.nextNavigationSection();
                break;
            case 'Key_Back':
                vm.stopContent();
                vm.backToSubmenu();
                break;
            case 'Key_Down':
                if (vm.selectedCategoryIndex < vm.tvChannelCategoriesList.length - 1) {
                    vm.changeCategory('down');
                    // vm.scrollDown();
                    vm.scrollCategoryList();
                    vm.scrollChannelList();
                }
                break;
            case 'Key_Up':
                if (vm.selectedCategoryIndex > 0) {
                    vm.changeCategory('up');
                    // vm.scrollUp();
                    vm.scrollCategoryList();
                    vm.scrollChannelList();
                }
                break;
        }
    };

    vm.handleChannelNavigation = function (args) {
        vm.scrollHeight = document.getElementById('tvChannelContent').scrollHeight;
        switch (args.keyname) {
            case 'Key_Right':
                if (vm.selectedTvChannelIndex < vm.selectedCategoryChannels.length - 1) {
                    vm.changeChannel('right');
                    if (vm.selectedTvChannelIndex % vm.spliceSize == 0) {
                        //vm.scrollDown();
                        vm.scrollChannelList();
                    }
                }
                vm.allowFullscreen = false;
                break;
            case 'Key_Left':
                if (vm.selectedTvChannelIndex > 0) {
                    vm.changeChannel('left');
                    if (vm.selectedTvChannelIndex % vm.spliceSize == vm.spliceSize - 1) {
                        //vm.scrollUp();
                        vm.scrollChannelList();
                    }
                }
                vm.allowFullscreen = false;
                break;
            case 'Key_Ok':
                if (vm.allowFullscreen) {
                    try {
                        vm.setFullScreen();
                        vm.allowFullscreen = false;
                        vm.nextNavigationSection();
                    } catch (err) {
                        console.log(err.message);
                    }
                } else {
                    vm.playContent();
                    vm.allowFullscreen = true;
                }
                break;
            case 'Key_Back':
                if (vm.isCategorizedView) {
                    vm.prevNavigationSection();
                } else {
                    vm.stopContent();
                    vm.backToSubmenu();
                }
                vm.allowFullscreen = false;
                break;
            case 'Key_Down':
                if (vm.selectedTvChannelIndex + vm.spliceSize < vm.selectedCategoryChannels.length) {
                    vm.changeChannel('down');
                    //vm.scrollDown();
                    vm.scrollChannelList();
                }
                vm.allowFullscreen = false;
                break;
            case 'Key_Up':
                if (vm.selectedTvChannelIndex >= vm.spliceSize) {
                    vm.changeChannel('up');
                    //vm.scrollUp();
                    vm.scrollChannelList();
                }
                vm.allowFullscreen = false;
                break;
        }
    };

    vm.handleFullscreenNavigation = function (args) {
        vm.scrollHeight = document.getElementById('tvChannelContent').scrollHeight;
        switch (args.keyname) {
            case 'Key_Back':
            case 'Key_Ok':
            case 'Key_Home':
                try {
                    vm.currentSubtitleButtonIndex = 0;
                    vm.setSubtitle();
                } catch (err) {
                    console.log('-----Error in full screen navigation-----');
                    console.log(err);
                }

                document.getElementsByClassName("newWelcomeShow ")[0].style.display = "block";
                document.getElementById('mainTable').style.backgroundImage = vm.welcomeData.BackImage;
                vm.setPreviewScreen();
                vm.allowFullscreen = true;
                vm.prevNavigationSection();
                break;
            case 'Key_ChannelUp':
            case 'Key_Up':
                if (vm.selectedTvChannelIndex < vm.selectedCategoryChannels.length - 1) {
                    vm.changeChannel('right');
                    if (vm.selectedTvChannelIndex % vm.spliceSize == 0) {
                        //vm.scrollDown();
                        vm.scrollChannelList();
                    }
                    vm.playContent();
                    vm.setFullScreen();
                }
                break;
            case 'Key_ChannelDown':
            case 'Key_Down':
                if (vm.selectedTvChannelIndex > 0) {
                    vm.changeChannel('left');
                    if (vm.selectedTvChannelIndex % vm.spliceSize == vm.spliceSize - 1) {
                        //vm.scrollUp();
                        vm.scrollChannelList();
                    }
                    vm.playContent();
                    vm.setFullScreen();
                }
                break;
            case 'Key_Yellow':

                try {
                    if (vm.subtitleAudioTabs.length > 0) {
                        vm.nextNavigationSection();
                        vm.currentSubtitleAudioTabIndex = 0;
                        vm.currentSubtitleButtonIndex = 0;
                        vm.currentAudioButtonIndex = 0;
                        vm.openSubtitleAudioMenu();
                    }
                } catch (e) {
                    console.log('Error when trying to show subtitle menu:');
                    console.log(e.message);
                }
                break;
        }
    };

