    service.switchInput = function (params, callback, error) {
        console.log("***44 service.switchInput");
        console.log(params.sourceType);
        switch (params.sourceType) {
            case "HDMI":
                var param = {
                    "type": hcap.externalinput.ExternalInputType.HDMI,
                    "index": params.sourceIndex,
                    "onSuccess": function onSuccess() {
                        //ClientSideLogWriter("hcap.externalinput.ExternalInputType.HDMI onSuccess");

                        LogService.setInfoLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/lgUtil.js|*|$Method:service.switchInput|*|$Message : ExternalInputType.HDMI is run -> " }, function (result) {
                            console.log("js/services/util/lgUtil.js service.switchInput ExternalInputType.HDMI is run");
                        });
                        callback();
                    },
                    "onFailure": function onFailure(f) {
                        LogService.setErrorLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/lgUtil.js|*|$Method:service.switchInput|*|$Message : ExternalInputType.HDMI is not run -> " + f.errorMessage }, function (result) {
                            console.log("js/services/util/lgUtil.js service.switchInput ExternalInputType.HDMI is not run");
                            console.log(f.errorMessage);
                        });
                        console.log('Error on switchInput: ' + f.errorMessage);
                        error(f);
                    }
                };

                // SetDeviceStatus(sourceType, sourceIndex + 1);
                hcap.externalinput.setCurrentExternalInput(param);
                break;
            case "TV":
                hcap.externalinput.getCurrentExternalInput({
                    "onSuccess": function onSuccess(s) {
                        if (s.type != hcap.externalinput.ExternalInputType.TV) {
                            hcap.externalinput.setCurrentExternalInput({
                                "type": hcap.externalinput.ExternalInputType.TV,
                                "index": 0,
                                "onSuccess": function onSuccess() {
                                    console.log('Successfully switched to TV');
                                    LogService.setInfoLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/lgUtil.js|*|$Method:service.switchInput|*|$Message : ExternalInputType.TV is run -> " }, function (result) {
                                        console.log("js/services/util/lgUtil.js service.switchInput ExternalInputType.TV is run");
                                    });
                                    callback();
                                },
                                "onFailure": function onFailure(f) {
                                    // setTvExternalInput();
                                    LogService.setErrorLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/lgUtil.js|*|$Method:service.switchInput|*|$Message : ExternalInputType.TV is not run -> " + f.errorMessage }, function (result) {
                                        console.log("js/services/util/lgUtil.js service.switchInput ExternalInputType.TV is not run");
                                        console.log(f.errorMessage);
                                    });
                                    console.log("An error occured on setCurrentExternalInput: errorMessage = " + f.errorMessage);
                                    error(f);
                                }
                            });
                        } else {}
                    },
                    "onFailure": function onFailure(f) {
                        LogService.setErrorLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/lgUtil.js|*|$Method:service.switchInput|*|$Message : ExternalInputType.TV is not run -> " + f.errorMessage }, function (result) {
                            console.log("js/services/util/lgUtil.js service.switchInput ExternalInputType.TV is not run");
                            console.log(f.errorMessage);
                        });
                        console.log("An error occured on getCurrentExternalInput : errorMessage = " + f.errorMessage);
                    }
                });
                break;
        }
    };

