    service.handleKeypress = function (event) {
        console.log("event.keyCode");
        console.log(event.keyCode);
        var pressedKey = service.keyList.filter(function (key) {
            return key.keycode === event.keyCode;
        });

        var keycode;
        var pageUrl = window.location.href.split("/").pop();
        if (pressedKey.length > 0) {
            switch (pageUrl) {
                case "minibar":
                case "minibarorder":
                case "register":
                case "roomstatuslogin":

                    break;
                default:
                    if (pressedKey[0].keyvalue != null || pressedKey[0].keyvalue != undefined) {
                        keycode = pressedKey[0].keyvalue;

                        if (document.getElementById('customChannelNumber') != null) {
                            document.getElementById('customChannelNumber').style.backgroundImage = "url('images/channelNumberBG.png')";
                        }

                        if (service.keyPressList.length < 6) {
                            var listenChannelKey = function listenChannelKey(callback) {
                                setTimeout(function () {
                                    var setChannelKey = "";
                                    for (var i = 0; i < service.keyPressList.length; i++) {
                                        var setChannelKey = setChannelKey + service.keyPressList[i];
                                    }
                                    callback(setChannelKey);
                                }, 2000);
                            };

                            var selectChannelNumber = function selectChannelNumber(setChannelKey) {

                                setTimeout(function () {
                                    var deleteChannelKeyList = function deleteChannelKeyList() {

                                        setChannelKey = "";
                                        service.keyPressList = [];
                                        var lis = document.querySelectorAll('#customChannelNumber span');
                                        for (var i = 0; li = lis[i]; i++) {
                                            li.parentNode.removeChild(li);
                                        }

                                        document.getElementById('customChannelNumber').style.backgroundImage = "none";
                                    };

                                    switch (setChannelKey) {
                                        case "12345":
                                            deleteChannelKeyList();
                                            $state.go('welcome');
                                            break;
                                        case "123456":
                                            deleteChannelKeyList();
                                            location.reload();
                                            break;

                                        case "1472":
                                            var keyPanel = document.getElementById("customChannelNumber");
                                            keyPanel.style.visibility = "hidden";
                                            deleteChannelKeyList();
                                            $state.go('register');
                                            break;

                                        case "98765":
                                            deleteChannelKeyList();
                                            $state.go('roomstatuslogin');
                                            break;

                                        case "6464":
                                            deleteChannelKeyList();
                                            $state.go('minibar');
                                            break;

                                        default:

                                            var pageUrl = window.location.href.split("/").pop();
                                            var registerControl = pageUrl.split("#")[1];
                                            var registerLocationStatus = pageUrl.split("#")[0] == "register" && registerControl != undefined ? true : false;

                                            if (pageUrl == "minibar" || pageUrl == "minibarorder" || pageUrl == "register" || pageUrl == "roomstatuslogin" || registerLocationStatus) {
                                                // document.getElementById('pluginSefIPTV2').style.display = "none";
                                                document.getElementById("pluginSefPlayer").style.display = "none";
                                                document.getElementById("pluginSefIPTV2").style.display = "none";
                                                deleteChannelKeyList();
                                            } else {
                                                var defaultChannelId = parseInt(setChannelKey);

                                                $rootScope.$broadcast("tvChannelControl", "goChannel", 0, defaultChannelId);
                                                deleteChannelKeyList();
                                            }

                                            break;
                                    }
                                }, 1000);
                            };

                            service.keyPressList.push(keycode);

                            var li = document.createElement("span");

                            li.appendChild(document.createTextNode(keycode));

                            listenChannelKey(selectChannelNumber);
                        }
                    }
                    break;
            }
        }
    };

