    service.keyPressList = [];

    service.handleKeypress = function (event) {

        var pressedKey = service.keyList.filter(function (key) {
            return key.keycode === event.keyCode;
        });

        var keycode;
        var pageUrl = window.location.href.split("/").pop();
        if (pressedKey.length > 0) {
            switch (pageUrl) {
                case "minibar":
                case "minibarorder":
                case "register":
                case "roomstatuslogin":

                    break;
                default:
                    if (pressedKey[0].keyvalue != null || pressedKey[0].keyvalue != undefined) {
                        keycode = pressedKey[0].keyvalue;

                        if (document.getElementById('customChannelNumber') != null) {
                            document.getElementById('customChannelNumber').style.backgroundImage = "url('images/channelNumberBG.png')";
                        }

                        if (service.keyPressList.length < 6) {
                            var listenChannelKey = function listenChannelKey(callback) {
                                setTimeout(function () {
                                    var setChannelKey = "";
                                    for (var i = 0; i < service.keyPressList.length; i++) {
                                        var setChannelKey = setChannelKey + service.keyPressList[i];
                                    }
                                    callback(setChannelKey);
                                }, 2000);
                            };

                            var selectChannelNumber = function selectChannelNumber(setChannelKey) {

                                setTimeout(function () {
                                    var deleteChannelKeyList = function deleteChannelKeyList() {

                                        setChannelKey = "";
                                        service.keyPressList = [];
                                        var lis = document.querySelectorAll('#customChannelNumber span');
                                        for (var i = 0; li = lis[i]; i++) {
                                            li.parentNode.removeChild(li);
                                        }

                                        document.getElementById('customChannelNumber').style.backgroundImage = "none";
                                    };

                                    switch (setChannelKey) {
                                        case "12345":
                                            deleteChannelKeyList();
                                            $state.go('welcome');
                                            break;

                                        case "123456":
                                            deleteChannelKeyList();
                                            location.reload();
                                            break;

                                        case "1472":
                                            var keyPanel = document.getElementById("customChannelNumber");
                                            keyPanel.style.visibility = "hidden";
                                            deleteChannelKeyList();
                                            $state.go('register');
                                            break;

                                        case "98765":
                                            deleteChannelKeyList();
                                            $state.go('roomstatuslogin');
                                            break;

                                        case "6464":
                                            deleteChannelKeyList();
                                            $state.go('minibar');
                                            break;

                                        default:

                                            var pageUrl = window.location.href.split("/").pop();
                                            var registerControl = pageUrl.split("#")[1];
                                            var registerLocationStatus = pageUrl.split("#")[0] == "register" && registerControl != undefined ? true : false;

                                            if (pageUrl == "minibar" || pageUrl == "minibarorder" || pageUrl == "register" || pageUrl == "roomstatuslogin" || registerLocationStatus) {
                                                // document.getElementById('pluginSefIPTV2').style.display = "none";
                                                document.getElementById("pluginSefPlayer").style.display = "none";
                                                document.getElementById("pluginSefIPTV2").style.display = "none";
                                                deleteChannelKeyList();
                                            } else {
                                                var defaultChannelId = parseInt(setChannelKey);
                                                if (service.catType) {
                                                    LogService.setInfoLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/samsungUtil.js|*|$Method:handleKeypress|*|$Message : handleKeypress service.catType true " }, function (result) {
                                                        console.log('*** js/services/util/samsungUtil.js handleKeypress service.catType true');
                                                    });
                                                    $rootScope.$broadcast("tvChannelControl", "goChannel", 0, defaultChannelId);
                                                } else {
                                                    if (pageUrl.indexOf("tvpreview") != -1) {
                                                        LogService.setInfoLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/samsungUtil.js|*|$Method:handleKeypress|*|$Message : service.catType false - noncategory tvpreview" }, function (result) {
                                                            console.log('*** js/services/util/samsungUtil.js handleKeypress service.catType false - noncategory tvpreview');
                                                        });
                                                        $rootScope.$broadcast("tvChannelControl", "goChannel", 0, defaultChannelId);
                                                    } else {
                                                        LogService.setInfoLog({ "message": "*|$Stb:" + service.deviceInfo.stb + "|*|$Mac:" + service.deviceInfo.macAddr + "|*|$Path:js/services/util/samsungUtil.js|*|$Method:handleKeypress|*|$Message : service.catType false - noncategory tvpreview-1" }, function (result) {
                                                            console.log('*** js/services/util/samsungUtil.js handleKeypress service.catType false - noncategory tvpreview-1');
                                                        });
                                                        $rootScope.$broadcast("tvChannelControl", "goChannel", 0, defaultChannelId - 1);
                                                    }
                                                }

                                                deleteChannelKeyList();
                                            }

                                            break;
                                    }
                                }, 1000);
                            };

                            service.keyPressList.push(keycode);

                            var ul = document.getElementById("customChannelNumber");
                            var li = document.createElement("span");
                            li.appendChild(document.createTextNode(keycode));
                            ul.appendChild(li);

                            listenChannelKey(selectChannelNumber);
                        }
                    }
                    break;
            }
        }

        if (pressedKey.length > 0) {

            pressedKey = pressedKey[0].keyname;
            switch (pressedKey) {
                case 'Key_Volume_Up':
                    service.setVolumeLevelWithKeypress(0);
                    break;
                case 'Key_Volume_Down':
                    service.setVolumeLevelWithKeypress(1);
                    break;
                case 'Key_Back':
                    service.widgetAPI.blockNavigation(event);
                    break;
                case 'Key_Exit':
                    service.widgetAPI.blockNavigation(event);
                    break;
                case 'Key_Mute':
                    console.log('Key_Mute has pressed.');
                    service.switchVolumeMute();
                    break;
            }
        }
        // service.widgetAPI.blockNavigation(event);
    };

